function checkMessage(message) {
  // Turn the message string into an array
  const messageArray = message.split(" ")

  for (messageIndex = 0; messageIndex < messageArray.length; messageIndex++) {
    for (blockedWordsIndex = 0; blockedWordsIndex < blockedWords.length; blockedWordsIndex++) {
      if (messageArray[messageIndex].search(blockedWords[blockedWordsIndex][0]) != -1) {
        // See what type of blocked it is (1 = curse, 2 = banned character)
        switch (blockedWords[blockedWordsIndex][1]) {
          case 1 /* Curse Words */:
            let starsCount = "";
            for (let hashIndex = 0; hashIndex < messageArray[messageIndex].length; hashIndex++) {
              starsCount = starsCount + "*";
            }
            messageArray[messageIndex] = starsCount;
            console.log("Curse");
            break;
          case 2 /* Banned Characters */:
            messageArray[messageIndex] = "";
            console.log("Blocked character");
            break;
          case 3 /* Easter-Egg */:
            alert("You found an Easter-Egg, Good job!");
            break;
          default /* Out of Bounds */:
            console.error(`Value is out of bounds for second dimension of \"blockedWords\" array.`);
            alert(`A server-side error has occured.`);
            return "";
            break;
        }
      }
    }
  }
  const filteredMessage = messageArray.join(" ");
  return filteredMessage;
}